import React, { useEffect, useState } from "react";
import { pickingPaperEffect } from "../utils/paper-sound-effect";
import "./note.css";

const Note = ({ paragraph, notePostionClassName, collectNote }) => {
  const [noteShown, setNoteShown] = useState(false);

  useEffect(() => {
    const note = document.getElementById("note");
    note.classList.add(notePostionClassName);
    // note.style.margin = notePostion?.margin;
    // note.style.top = notePostion?.top;
    // note.style.left = notePostion?.left;
    // note.style.scale = notePostion?.scale;
  });

  const showNote = () => {
    if (!noteShown) {
      pickingPaperEffect();
      setNoteShown(true);
      const note = document.getElementById("note");
      note.classList.add("test"); //change class name
    }
  };

  return (
    <div id="note" className="content-main" onClick={() => showNote()}>
      <div className="content-box">
        <h2>About me</h2>
        <p>{paragraph}</p>
        <button onClick={() => collectNote(true)}>Collect</button>
      </div>
    </div>
  );
};

export default Note;
